import React, { useState, useEffect, useRef } from 'react';
import { useReactToPrint } from 'react-to-print'; // ‚òÖ Ï∂îÍ∞Ä
import { ResearchNote } from './ResearchNote';     // ‚òÖ Î∞©Í∏à ÎßåÎì† Ïª¥Ìè¨ÎÑåÌä∏ import
import './App.css'; 

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null); // Ï∂úÎ†•Ìï† ÏÇ¨Îûå ÏÑ†ÌÉù

  // Ïù∏ÏáÑÎ•º ÏúÑÌïú Ref ÏÉùÏÑ±
  const printRef = useRef();

  // ‚òÖ Ïù∏ÏáÑ Í∏∞Îä• ÏÑ§Ï†ï
  const handlePrint = useReactToPrint({
    content: () => printRef.current, // Ïù∏ÏáÑÌï† ÏòÅÏó≠ ÏßÄÏ†ï
    documentTitle: `Ïó∞Íµ¨ÎÖ∏Ìä∏_${selectedUser?.name}`, // Ï†ÄÏû•Îê† ÌååÏùºÎ™Ö
  });

  // ÏÇ¨Ïö©ÏûêÍ∞Ä "Ï∂úÎ†•" Î≤ÑÌäºÏùÑ ÎàÑÎ•¥Î©¥ Ïã§ÌñâÎêòÎäî Ìï®Ïàò
  const clickPrint = (user) => {
    setSelectedUser(user); // 1. Îç∞Ïù¥ÌÑ∞Î•º Ï±ÑÏõåÎÑ£Í≥†
    // state Î≥ÄÍ≤Ω ÌõÑ Î†åÎçîÎßÅ ÏãúÍ∞ÑÏùÑ ÏïÑÏ£º Ï°∞Í∏à Í∏∞Îã§Î†∏Îã§Í∞Ä Ïù∏ÏáÑ Ï∞ΩÏùÑ ÎùÑÏõÄ
    setTimeout(() => {
      handlePrint();
    }, 100);
  };

  // ... (fetchUsers Ìï®ÏàòÎäî Í∏∞Ï°¥Í≥º ÎèôÏùº) ...
  // (ÏΩîÎìú Ï§ëÎûµ: useEffect, fetchUsers Îì±)

  // (ÌÖåÏä§Ìä∏Ïö© ÌïòÎìúÏΩîÎî© Îç∞Ïù¥ÌÑ∞, Ïã§Ï†úÎ°† fetchUsers ÏÇ¨Ïö©ÌïòÏÑ∏Ïöî)
  useEffect(() => {
      setUsers([
          {id: 1, department: 'Í∞úÎ∞ú1ÌåÄ', name: 'Î∞ïÎØºÏàò', email: 'min@test.com'},
          {id: 2, department: 'Ïã†ÏÇ¨ÏóÖÌåÄ', name: 'ÍπÄÏ≤†Ïàò', email: 'cheol@test.com'},
      ]);
  }, []);

  return (
    <div className="App" style={{ padding: '20px' }}>
      {/* ... (Ï†úÎ™© Îì± Í∏∞Ï°¥ UI) ... */}

      <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th>Î∂ÄÏÑú</th>
            <th>Ïù¥Î¶Ñ</th>
            <th>Ïù¥Î©îÏùº</th>
            <th>ÏûëÏóÖ</th> {/* Î≤ÑÌäº ÎÑ£ÏùÑ Ïπ∏ Ï∂îÍ∞Ä */}
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.department}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td style={{ textAlign: 'center' }}>
                {/* ‚òÖ Ï∂úÎ†• Î≤ÑÌäº ÏÉùÏÑ± */}
                <button 
                  onClick={() => clickPrint(user)}
                  style={{
                    backgroundColor: '#4CAF50', 
                    color: 'white', 
                    border: 'none', 
                    padding: '8px 16px', 
                    cursor: 'pointer',
                    borderRadius: '4px'
                  }}
                >
                  üñ®Ô∏è Ïó∞Íµ¨ÎÖ∏Ìä∏ Ï∂úÎ†•
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ‚òÖ Ï§ëÏöî: ÌôîÎ©¥ÏóêÎäî Î≥¥Ïù¥ÏßÄ ÏïäÏßÄÎßå, Ïù∏ÏáÑ Î≤ÑÌäºÏùÑ ÎàÑÎ•¥Î©¥ Ï±ÑÏõåÏßÄÎäî Ïà®Í≤®ÏßÑ ÏòÅÏó≠ 
        (display: noneÏùÑ ÌïòÎ©¥ Ïù∏ÏáÑÎèÑ ÏïàÎêòÎØÄÎ°ú, overflowÎ°ú Ïà®Í∏∞Îäî Ìä∏Î¶≠ ÏÇ¨Ïö©ÌïòÍ±∞ÎÇò
         CSS media queryÎ°ú ÌôîÎ©¥ÏóêÏÑúÎßå ÏïàÎ≥¥Ïù¥Í≤å Ï≤òÎ¶¨Ìï©ÎãàÎã§.)
      */}
      <div style={{ display: 'none' }}>
        <ResearchNote 
          ref={printRef} 
          user={selectedUser} 
          date={new Date().toLocaleDateString()} 
        />
      </div>
    </div>
  );
}

