import React, { useState, useEffect } from 'react';
import { ResearchNote } from './ResearchNote'; // Ïª¥Ìè¨ÎÑåÌä∏ import ÌôïÏù∏
import './App.css'; 

function App() {
  const [users, setUsers] = useState([]);      
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);

  // ‚òÖ Ïù∏ÏáÑÌï† ÏÇ¨Îûå Ï†ïÎ≥¥Î•º Îã¥ÏùÑ state
  const [printUser, setPrintUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch('/api/users');
      if (!response.ok) throw new Error('Îç∞Ïù¥ÌÑ∞ Ïã§Ìå®');
      const data = await response.json();
      setUsers(data); 
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // ‚òÖ [ÏàòÏ†ïÎê®] ÎùºÏù¥Î∏åÎü¨Î¶¨ ÏóÜÏù¥ Ïù∏ÏáÑÌïòÎäî Ìï®Ïàò
  const handlePrint = (user) => {
    setPrintUser(user); // 1. Ïù∏ÏáÑÌï† ÏÇ¨Îûå Îç∞Ïù¥ÌÑ∞ ÏÑ∏ÌåÖ

    // 2. Îç∞Ïù¥ÌÑ∞Í∞Ä Î†åÎçîÎßÅÎê† ÏãúÍ∞ÑÏùÑ 0.1Ï¥à Ï§Ä Îí§ Ïù∏ÏáÑ Ï∞Ω ÎùÑÏö∞Í∏∞
    setTimeout(() => {
      window.print();
    }, 100);
  };

  if (loading) return <h2>‚è≥ Î°úÎî© Ï§ë...</h2>;
  if (error) return <h2 style={{ color: 'red' }}>{error}</h2>;

  return (
    <div className="App" style={{ padding: '20px' }}>

      {/* ÌôîÎ©¥Ïóê Î≥¥Ïù¥Îäî Î™©Î°ù ÏòÅÏó≠ (Ïù∏ÏáÑÌï† ÎïåÎäî Ïà®Í≤®Ïßê) */}
      <div className="no-print">
        <h1>üè¢ Zoom ÏßÅÏõê Î∞è Î∂ÄÏÑú ÌòÑÌô©</h1>
        <table border="1" cellPadding="10" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ backgroundColor: '#f0f0f0' }}>
              <th>Î∂ÄÏÑú</th>
              <th>Ïù¥Î¶Ñ</th>
              <th>Ïù¥Î©îÏùº</th>
              <th>ÏûëÏóÖ</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.department}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td style={{ textAlign: 'center' }}>
                  {/* ‚òÖ ÌÅ¥Î¶≠ÌïòÎ©¥ handlePrint Ïã§Ìñâ */}
                  <button 
                    onClick={() => handlePrint(user)}
                    style={{ cursor: 'pointer', padding: '5px 10px' }}
                  >
                    üñ®Ô∏è Ï∂úÎ†•
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* ‚òÖ [Ï§ëÏöî] Ïù∏ÏáÑÏö© ÏòÅÏó≠ (ÌèâÏÜåÏóî Ïïà Î≥¥ÏûÑ) */}
      <div className="print-only">
        {printUser && (
          <ResearchNote 
            user={printUser} 
            date={new Date().toLocaleDateString()} 
          />
        )}
      </div>

    </div>
  );
}

export default App;
