import React, { useState, useEffect } from 'react';
import './App.css'; 

function App() {
  const [users, setUsers] = useState([]);      
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);     

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      // Node.js Î∞±ÏóîÎìú ÏÑúÎ≤ÑÎ°ú ÏöîÏ≤≠
      const response = await fetch('http://localhost:5000/api/users');

      if (!response.ok) {
        throw new Error('ÏÑúÎ≤ÑÏóêÏÑú Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }

      const data = await response.json();
      console.log("Î∞õÏïÑÏò® Îç∞Ïù¥ÌÑ∞:", data);

      // ‚ñº [ÏàòÏ†ï 1] ÏÑúÎ≤ÑÍ∞Ä Ïù¥Ï†ú { users: [...] }Í∞Ä ÏïÑÎãàÎùº Î∞∞Ïó¥ [...]ÏùÑ Î∞îÎ°ú Ï§çÎãàÎã§.
      setUsers(data); 

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <h2>‚è≥ ÏßÅÏõê Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</h2>;
  if (error) return <h2 style={{ color: 'red' }}>‚ùå ÏóêÎü¨ Î∞úÏÉù: {error}</h2>;

  return (
    <div className="App" style={{ padding: '20px' }}>
      <h1>üè¢ Zoom ÏßÅÏõê Î∞è Î∂ÄÏÑú ÌòÑÌô©</h1>
      <p>Ï¥ù {users.length}Î™ÖÏùò ÏÇ¨Ïö©ÏûêÍ∞Ä Í≤ÄÏÉâÎêòÏóàÏäµÎãàÎã§.</p>

      <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse', width: '100%', textAlign: 'left' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            {/* ‚ñº [ÏàòÏ†ï 2] Î∂ÄÏÑú(Department) Ïª¨Îüº Ï∂îÍ∞Ä Î∞è Ïù¥Î¶Ñ ÌÜµÌï© */}
            <th style={{ width: '20%' }}>üìÇ Î∂ÄÏÑú (Dept)</th>
            <th style={{ width: '20%' }}>Ïù¥Î¶Ñ (Name)</th>
            <th>Ïù¥Î©îÏùº</th>
            <th>ÏÇ¨Ïö©Ïûê ID</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              {/* ‚ñº [ÏàòÏ†ï 3] ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏïÑÏò® ÏÉàÎ°úÏö¥ Îç∞Ïù¥ÌÑ∞ ÌÇ§(Key) ÏÇ¨Ïö© */}
              <td style={{ fontWeight: 'bold', color: '#0056b3' }}>
                {user.department}
              </td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td style={{ color: '#888', fontSize: '0.8em' }}>{user.id}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <br />
      <button onClick={fetchUsers} style={{ padding: '10px 20px', cursor: 'pointer' }}>
        üîÑ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
      </button>
    </div>
  );
}

export default App;
