import React, { useState, useEffect } from 'react';
import zoomSdk from '@zoom/appssdk'; // 👈 SDK 불러오기
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [notes, setNotes] = useState([]);
  const [status, setStatus] = useState('Zoom 연결 대기 중...');

  useEffect(() => {
    async function initZoom() {
      try {
        // 1. Zoom SDK 설정 (필수)
        const configResponse = await zoomSdk.config({
          size: { width: 480, height: 720 }, // 사이드바 크기
          capabilities: [
            'connect', // 연결 권한
            'getRunningContext', // 현재 상태 확인
            'authorize' // ⭐ 로그인 요청
          ]
        });
        console.log('Zoom Config 완료:', configResponse);

        // 2. 현재 Zoom에서 실행 중인지 확인
        // (브라우저에서 테스트 중이면 이 단계는 실패할 수 있음)
        const context = await zoomSdk.getRunningContext();
        console.log('현재 환경:', context);

        // 3. 로그인(권한 위임) 시도
        // 사용자가 이미 Zoom에 로그인해 있으므로, 바로 토큰을 달라고 요청함
        setStatus('사용자 정보 가져오는 중...');

        // 이 함수가 실행되면 Zoom 내부에서 'Authorize' 과정이 진행됨
        // 리다이렉트 없이 내부적으로 처리하거나, 필요한 경우에만 줌 서버를 다녀옴
        const authResponse = await zoomSdk.authorize({
           state: 'zoomnote_init',
           codeChallenge: '...', // PKCE 보안이 필요하지만, 일단 생략(간소화)
        });

        // 🚨 주의: SDK authorize는 보통 서버로 리다이렉트 시킵니다.
        // 하지만 지금 우리는 간단한 확인을 위해 "이미 로그인된 척" 테스트 버튼을 유지하겠습니다.
        // (완전한 SDK 연동은 서버의 인증 구조도 PKCE로 바꿔야 해서 복잡해집니다.)

      } catch (e) {
        console.error('Zoom SDK 오류:', e);
        setStatus('웹 브라우저에서 테스트 중이신가요?');
      }
    }

    initZoom();
  }, []);

  // ... (기존 메모 조회 및 저장 로직 유지) ...
  // 편의상 아까 만든 테스트용 버튼은 그대로 두되, 실제 앱에서는 숨깁니다.

  return (
    <div className="App" style={{ padding: '20px' }}>
      <h3>🚀 Zoom Research Note</h3>
      <p style={{ fontSize: '12px', color: '#666' }}>상태: {status}</p>

      {/* 로그인된 정보가 있으면 환영 메시지 */}
      {user ? (
        <div style={{ background: '#e3f2fd', padding: '10px', borderRadius: '5px', marginBottom: '20px' }}>
          <strong>👋 안녕하세요, {user.name}님!</strong><br/>
          <span style={{ fontSize: '12px' }}>{user.department} / {user.role}</span>
        </div>
      ) : (
        // 정보가 없으면 로그인 버튼 (Zoom 안에서는 자동화 가능하지만, 테스트용으로 유지)
        <a href="http://localhost:8080/login">
          <button style={{ width:'100%', background:'#2D8CFF', color:'white', padding:'10px', border:'none', borderRadius:'5px', cursor:'pointer' }}>
             🔵 Zoom 연동 시작하기
          </button>
        </a>
      )}

      {/* ... 노트 목록 렌더링 ... */}
      {/* (아까 작성하신 노트 목록 코드 그대로 유지) */}
    </div>
  );
}

export default App;
