import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [memos, setMemos] = useState([]);
  const [inputText, setInputText] = useState('');

  // 1. Ï≤òÏùå ÏºúÏ°åÏùÑ Îïå ÏÑúÎ≤ÑÏóêÏÑú Î©îÎ™® Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    fetch('http://localhost:8080/api/memos')
      .then((res) => res.json())
      .then((data) => setMemos(data))
      .catch((err) => console.error("Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", err));
  }, []);

  // 2. Ï†ÄÏû• Î≤ÑÌäº ÎàåÎ†ÄÏùÑ Îïå ÏÑúÎ≤ÑÎ°ú Î©îÎ™® Î≥¥ÎÇ¥Í∏∞
  const saveMemo = () => {
    if (!inputText) return; // ÎπàÏπ∏Ïù¥Î©¥ Ï†ÄÏû• ÏïàÌï®

    fetch('http://localhost:8080/api/memos', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ content: inputText }),
    })
      .then((res) => {
        if (res.ok) {
          alert('Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!');
          setInputText(''); // ÏûÖÎ†•Ï∞Ω ÎπÑÏö∞Í∏∞
          window.location.reload(); // ÏÉàÎ°úÍ≥†Ïπ®Ìï¥ÏÑú Î™©Î°ù Í∞±Ïã†
        }
      })
      .catch((err) => console.error("Ï†ÄÏû• Ïã§Ìå®:", err));
  };

  return (
    <div className="App" style={{ padding: '20px' }}>
      <h1>üìù ZoomNote (Minsu)</h1>

      {/* ÏûÖÎ†•Ï∞Ω */}
      <div style={{ marginBottom: '20px' }}>
        <input 
          type="text" 
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Ïò§ÎäòÏùò Î©îÎ™®Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
          style={{ padding: '10px', width: '300px', marginRight: '10px' }}
        />
        <button onClick={saveMemo} style={{ padding: '10px 20px' }}>
          Ï†ÄÏû•
        </button>
      </div>

      {/* Î©îÎ™® Î™©Î°ù */}
      <div style={{ borderTop: '1px solid #ccc', paddingTop: '10px' }}>
        {memos.map((memo) => (
          <div key={memo.id} style={{ 
            background: '#f9f9f9', 
            border: '1px solid #ddd', 
            padding: '10px', 
            marginBottom: '10px',
            borderRadius: '5px'
          }}>
            <p style={{ margin: 0, fontWeight: 'bold' }}>{memo.content}</p>
            <small style={{ color: '#888' }}>{new Date(memo.created_at).toLocaleString()}</small>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
