import React, { useState, useEffect } from 'react';
import './App.css'; // Í∏∞Î≥∏ CSS (ÌïÑÏöîÌïòÎ©¥ ÏàòÏ†ï)

function App() {
  const [users, setUsers] = useState([]);      // ÏÇ¨Ïö©Ïûê Î™©Î°ù Ï†ÄÏû•
  const [loading, setLoading] = useState(true); // Î°úÎî© ÏÉÅÌÉú
  const [error, setError] = useState(null);     // ÏóêÎü¨ ÏÉÅÌÉú

  // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ï≤òÏùå ÌôîÎ©¥Ïóê ÎÇòÌÉÄÎÇ† Îïå Ïã§Ìñâ
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      // Node.js Î∞±ÏóîÎìú ÏÑúÎ≤ÑÎ°ú ÏöîÏ≤≠
      const response = await fetch('http://localhost:5000/api/users');

      if (!response.ok) {
        throw new Error('ÏÑúÎ≤ÑÏóêÏÑú Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }

      const data = await response.json();
      console.log("Î∞õÏïÑÏò® Îç∞Ïù¥ÌÑ∞:", data);

      // Zoom API Íµ¨Ï°∞ÏÉÅ users Î∞∞Ïó¥ÏùÄ data.usersÏóê Îì§Ïñ¥ÏûàÏäµÎãàÎã§.
      setUsers(data.users || []); 
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <h2>‚è≥ ÏßÅÏõê Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</h2>;
  if (error) return <h2 style={{ color: 'red' }}>‚ùå ÏóêÎü¨ Î∞úÏÉù: {error}</h2>;

  return (
    <div className="App" style={{ padding: '20px' }}>
      <h1>üè¢ Zoom ÏßÅÏõê Î™©Î°ù (Internal App)</h1>
      <p>Ï¥ù {users.length}Î™ÖÏùò ÏÇ¨Ïö©ÏûêÍ∞Ä Í≤ÄÏÉâÎêòÏóàÏäµÎãàÎã§.</p>

      <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th>Ïù¥Î¶Ñ (First Name)</th>
            <th>ÏÑ± (Last Name)</th>
            <th>Ïù¥Î©îÏùº</th>
            <th>ÏÇ¨Ïö©Ïûê ID</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.first_name}</td>
              <td>{user.last_name}</td>
              <td>{user.email}</td>
              <td style={{ color: '#888' }}>{user.id}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <br />
      <button onClick={fetchUsers} style={{ padding: '10px 20px', cursor: 'pointer' }}>
        üîÑ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
      </button>
    </div>
  );
}

export default App;
